import { useDispatch, useSelector } from "react-redux";
import { RootState, AppDispatch } from "../store/store";
import { setUser } from "../store/userSlice";
import { TextField, Button, Box, Typography } from "@mui/material";

const UserForm = () => {
  const dispatch = useDispatch<AppDispatch>();
  const user = useSelector((state: RootState) => state.user);

  if (!user) {
    return <Typography variant="h5">Loading...</Typography>; 
  }

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(setUser({ [e.target.name]: e.target.value }));
  };

  const handleSubmit = () => {
    localStorage.setItem("user", JSON.stringify(user)); 
    alert("Profile updated successfully!");
  };

  return (
    <Box sx={{ maxWidth: 400, margin: "auto", mt: 4 }}>
      <Typography variant="h5" textAlign="center" gutterBottom>
        User Form
      </Typography>
      <TextField label="User ID" name="id" value={user.id} fullWidth margin="normal" disabled />
      <TextField label="Name" name="name" value={user.name} onChange={handleChange} fullWidth margin="normal" />
      <TextField label="Email" name="email" value={user.email} onChange={handleChange} fullWidth margin="normal" />
      <TextField label="Address" name="address" value={user.address} onChange={handleChange} fullWidth margin="normal" />
      <TextField label="Phone" name="phone" value={user.phone} onChange={handleChange} fullWidth margin="normal" />
      <Button onClick={handleSubmit} variant="contained" fullWidth sx={{ mt: 2 }}>
        Save
      </Button>
    </Box>
  );
};

export default UserForm;
