import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface UserState {
  user: { email: string } | null;
  userData: Array<{ id: string; name: string; email: string; address: string; phone: string }>;
}

const initialState: UserState = {
  user: null,
  userData: [],
};

const userSlice = createSlice({
  name: 'user',
  initialState,
  reducers: {
    loginUser: (state, action: PayloadAction<{ email: string }>) => {
      state.user = action.payload;
    },
    logoutUser: (state) => {
      state.user = null;
    },
    addUserData: (state, action: PayloadAction<{ id: string; name: string; email: string; address: string; phone: string }>) => {
      state.userData.push(action.payload);
    },
  },
});

export const { loginUser, logoutUser, addUserData } = userSlice.actions;
export default userSlice.reducer;
